import React, {useState,useEffect} from 'react';
import axios from 'axios';

const Goods = () => {
    const [posts,setPosts] = useState([]);
    const [error,setError] = useState(null);
    const [goodsFormData,setFormData] = useState({
      goodsName:"",
      goodsSize:"",
      goodsDesc:"",
      goodsUnitOfMeas:"",
      availableStock:"0",
      qtySold:"0"
    });
    const [addGoodsResponse,setResponse]= useState(null);
    const handleChange = (e) =>{
      setFormData({...goodsFormData,[e.target.name]:e.target.value});
    };
    const handleSubmit = async (e) =>{
      e.preventDefault();

    try {
      const res = await fetch("/addGoods", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(goodsFormData),
      });

      const data = await res.json();
      setResponse(data);
      alert("Form submitted successfully!");
    } catch (error) {
      console.error("Error:", error);
      alert("Failed to submit form");
    }
    }
    useEffect(()=>{
      axios.get('/listGoods')
      .then((response) => {
          console.log(response);
          setPosts(response.data[0]);
      })
      .catch((err) => {
          setError(err.message);
      });
    },[]);





    return <div className="container">

      <div className="row">
        <div className="col-sm-12">
          {error && <p style={{ color: 'red' }}>Error: {error}</p>}
          <ul className="list-group">
              {posts.map((post,index) => (

                <li className={post['goodsStatus']=='ACTIVE'?"list-group-item border-success":"list-group-item border-danger"} key={index}>
                  <div className="row">
                    <div className="col-sm-2 text-start">
                    <label className="fw-bold">Name</label>
                    <p>{post['goodsName']}</p>
                    </div>  
                    <div className="col-sm-2 text-start">
                    <label className='fw-bold'>Size</label>
                    <p>{post['goodsSize']}</p>
                    </div>
                    <div className="col-sm-3 text-start">
                    <label className='fw-bold'>Description</label>
                    <p>{post['goodsDesc']}</p>
                    </div>
                    <div className='col-sm-2 text-start'>
                      <label className='fw-bold'>Available Stock</label>
                      <p>{post['availableStock']}{post['goodsUnitOfMeasurement']}</p>
                    </div>
                    <div className='col-sm-2 text-start'>
                      <label className='fw-bold'>Quantity Sold</label>
                      <p>{post['qtySold']}{post['goodsUnitOfMeasurement']}</p>
                    </div>
                    <div className="col-sm-1 text-start">
                    <i class="bi bi-pencil"></i>&nbsp;
                    {/* <p>{post['goodsId']}</p> */}
                    <i class="bi bi-trash"></i>

                    </div>
                  </div>
                  </li>
              ))}
            </ul>
            <div className="floating-button"><i  data-bs-toggle="modal" data-bs-target="#formModal" class="bi bi-plus-circle"></i></div>   


<div class="modal fade" id="formModal" tabindex="-1" aria-labelledby="formModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="formModalLabel">User Form</h5>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">

        <form id="modalForm" onSubmit={handleSubmit}>
          <div class="mb-3">
            <label for="name" class="form-labe">Good Name</label>
            <input type="text" class="form-control" id="goodsName" name="goodsName" value={goodsFormData.goodsName} placeholder="Enter your good name" required/>
          </div>
          <div class="mb-3">
            <label for="goodsSize" class="form-label">Good Size</label>
            <input type="text" class="form-control" id="goodsSize" name="goodsSize" value={goodsFormData.goodsSize} placeholder="Enter your good size" required/>
          </div>
          <div class="mb-3">
            <label for="goodsDesc" class="form-label">Good Description</label>
            <input type="text" class="form-control" id="goodsDesc" name="goodsDesc" value={goodsFormData.goodsDesc} placeholder="Enter your good description" required/>
          </div>
          <div class="mb-3">
            <label for="goodsUnitOfMeas" class="form-label">Good Unit</label>
            <input type="text" class="form-control" id="goodsUnitOfMeas" name="goodsUnitOfMeas" value={goodsFormData.goodsUnitOfMeas} placeholder="Enter your good unit" required/>
          </div>
          <div class="mb-3">
            <label for="availableStock" class="form-label">Good Available Stock</label>
            <input type="hidden" class="form-control" id="availableStock" name="availableStock" value={goodsFormData.availableStock} placeholder="Enter Available Stock" required/>
          </div>
          <div class="mb-3">
            <label for="qtySold" class="form-label">Quantity Sold</label>
            <input type="hidden" class="form-control" id="qtySold" name="goodsSize" value={goodsFormData.goodsSize} placeholder="Enter your good size" required/>
          </div>





          <button type="submit" class="btn btn-success">Submit</button>
        </form>
      </div>
    </div>
  </div>
</div>
        </div>



      </div>
    </div>;
  };

  export default Goods;
